import React, { useState } from "react";
import styled from "styled-components";
import Modal from "./commons/Modalportfolio";
import Skill from "./portfolio/Skill";
import Experience from "./portfolio/Experience";

const WholeBack = styled.div`
  display: flex;
  justify-content: space-between;
  padding-top: 5rem;
`;

const ImageContainer = styled.div`
  cursor: pointer;

  &:hover {
    transform: translateZ(10px); /* 이미지를 앞으로 튀어나오게 만듭니다. */
  }
`;

const ImgBox = styled.div`
  display: flex;
  align-items: row;
  justify-content: space-around;
  img {
    width: 10rem;
    height: auto;
  }
`;

function PortfolioModal() {
  const [isModalOpen, setIsModalOpen] = useState(true);
  const [isExModalOpen, setIsExModalOpen] = useState(false);

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const exOpenModal = () => {
    setIsExModalOpen((prevModal) => !prevModal);
  };

  return (
    <>
      {isModalOpen && <Modal closeModal={closeModal} />}
      {!isModalOpen && (
        <>
          {!isExModalOpen ? (
            <WholeBack>
              <Skill exOpenModal={exOpenModal} />
              <Experience />
            </WholeBack>
          ) : (
            <WholeBack>
              <Skill />
            </WholeBack>
          )}
        </>
      )}
    </>
  );
}

export default PortfolioModal;
