import React, { useState } from "react";
import styled from "styled-components";
import Mario from "./photo/mariogif.gif";
import FrontEndImage from "./photo/frontend.jpg"; // Frontend 이미지 추가
import DataImageSrc from "./photo/data.jpg"; // Data 이미지 추가
import ProjectImageSrc from "./photo/project.jpg"; // Project 이미지 추가

const WholeBack = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(58, 58, 58, 0.673);
  z-index: 999;
`;

const ModalBackground = styled.div`
  width: 35%;
  height: 65vh;
  background-color: rgba(0, 0, 255, 0.5);
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 10px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
`;

const ModalContainer = styled.div`
  background-color: #ccc;
  width: 100%;
  height: 50vh;
  margin-bottom: -2rem;
  max-width: 400px;
  padding: 20px;
  text-align: center;
  border-radius: 3px;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
`;

const ModalText = styled.div`
  font-size: 25px;
  margin-bottom: 20px;
`;

const ButtonContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Button = styled.button`
  padding: 10px 30px;
  margin: 10px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const CloseButton = styled.button`
  background-color: red;
  color: white;
  border: none;
  border-radius: 3px;
  border-color: white;
  width: 30px;
  height: 30px;
  position: absolute;
  top: 127px;
  right: 435px;
`;

const SkillImage = styled.div`
  background-image: url(${FrontEndImage});
  background-size: cover;
  border-radius: 0.2rem;
  width: 150px;
  height: 150px;
  margin: 20px;
  transition: transform 0.3s ease;
  &:hover {
    transform: scale(1.2);
  }
  opacity: ${({ isModalOpen }) => (isModalOpen ? "1" : "0")};
  pointer-events: ${({ isModalOpen }) => (isModalOpen ? "auto" : "none")};
`;

const DataImage = styled.div`
  background-image: url(${DataImageSrc});
  background-size: cover;
  border-radius: 0.2rem;
  width: 150px;
  height: 150px;
  margin: 20px;
  transition: transform 0.3s ease;
  &:hover {
    transform: scale(1.2);
  }
  opacity: ${({ isModalOpen }) => (isModalOpen ? "1" : "0")};
  pointer-events: ${({ isModalOpen }) => (isModalOpen ? "auto" : "none")};
`;

const ProjectImage = styled.div`
  background-image: url(${ProjectImageSrc});
  background-size: cover;
  border-radius: 0.2rem;
  width: 150px;
  height: 150px;
  margin: 20px;
  transition: transform 0.3s ease;
  &:hover {
    transform: scale(1.2);
  }
  opacity: ${({ isModalOpen }) => (isModalOpen ? "1" : "0")};
  pointer-events: ${({ isModalOpen }) => (isModalOpen ? "auto" : "none")};
`;

function PortfolioModal() {
  const [isModalOpen, setIsModalOpen] = useState(true);
  const closeModal = () => {
    setIsModalOpen(false);
  };
  return (
    <>
      {isModalOpen && (
        <WholeBack>
          <ModalBackground>
            <ModalContainer>
              <CloseButton onClick={closeModal}>x</CloseButton>
              <ModalText>What do you want to know?</ModalText>
              <MarioImage />
              <ButtonContainer>
                <Button onClick={() => alert("안다면 더 알아보도록 합시다.")}>
                  SKILLS
                </Button>
                <Button
                  onClick={() => alert("모른다면 지금부터 알아보도록 합시다.")}
                >
                  EXPERIENCE
                </Button>
              </ButtonContainer>
            </ModalContainer>
          </ModalBackground>
        </WholeBack>
      )}
      {!isModalOpen && (
        <div>
          <SkillImage isModalOpen={isModalOpen} />
          <DataImage isModalOpen={isModalOpen} />
          <ProjectImage isModalOpen={isModalOpen} />
        </div>
      )}
    </>
  );
}

export default PortfolioModal;
