import React from "react";
import styled from "styled-components";

const ModalBackground = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 255, 0.5); /* 파란색 배경 */
  display: flex;
  justify-content: center;
  align-items: center;
`;

const ModalContainer = styled.div`
  background-color: #ccc; /* 회색 네모 */
  width: 90%;
  max-width: 400px;
  padding: 20px;
  text-align: center;
  border-radius: 10px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
`;

const ModalText = styled.div`
  font-size: 18px;
  margin-bottom: 20px;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Button = styled.button`
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const CloseButton = styled.button`
  background-color: red;
  color: white;
  border: none;
  border-radius: 50%;
  width: 30px;
  height: 30px;
  position: absolute;
  top: 10px;
  right: 10px;
`;

function PortfolioModal() {
  return (
    <ModalBackground>
      <ModalContainer>
        <CloseButton onClick={() => alert("Close button clicked")}>
          x
        </CloseButton>
        <ModalText>Do you know what my skills are?</ModalText>
        <ButtonContainer>
          <Button onClick={() => alert("안다면 더 알아보도록 합시다.")}>
            Yes
          </Button>
          <Button onClick={() => alert("모른다면 지금부터 알아보도록 합시다.")}>
            No
          </Button>
        </ButtonContainer>
      </ModalContainer>
    </ModalBackground>
  );
}

export default PortfolioModal;
