import React, { useState } from "react";
import styled from "styled-components";
import Mario from "./photo/mariogif.gif";

const WholeBack = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  position: fixed; /* 모달을 고정 위치로 표시 */
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.5); /* 회색 투명 배경 */
  z-index: 999; /* 다른 요소 위에 나타나도록 함 */
`;
const ModalBackground = styled.div`
  width: 35%;
  height: 65vh;
  background-color: rgba(0, 0, 255, 0.5); /* 파란색 배경 */
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 10px;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
`;

const ModalContainer = styled.div`
  background-color: #ccc; /* 회색 네모 */
  width: 100%;
  height: 50vh;
  margin-bottom: -2rem;
  max-width: 400px;
  padding: 20px;
  text-align: center;
  border-radius: 3px;
`;

const ModalText = styled.div`
  font-size: 25px;
  margin-bottom: 20px;
`;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Button = styled.button`
  padding: 10px 30px;
  margin: 10px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  margin-top: 15rem;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
`;

const CloseButton = styled.button`
  background-color: red;
  color: white;
  border: none;
  border-radius: 3px;
  border-color: white;
  width: 30px;
  height: 30px;
  position: absolute;
  top: 127px;
  right: 435px;
`;

const MarioImage = styled.div`
  background-image: url(${Mario});
  background-size: cover;
  border-radius: 0.2rem;
  width: 25rem;
  height: 14rem;
  position: absolute;
  z-index: 0;
`;

function PortfolioModal() {
  const [isModalOpen, setIsModalOpen] = useState(true);
  const closeModal = () => {
    setIsModalOpen(false);
  };
  return (
    <WholeBack>
      {isModalOpen && (
        <ModalBackground>
          <ModalContainer>
            <CloseButton onClick={closeModal}>x</CloseButton>
            <ModalText>Do you know what my skills are?</ModalText>
            <MarioImage />
            <ButtonContainer>
              <Button onClick={() => alert("안다면 더 알아보도록 합시다.")}>
                Yes
              </Button>
              <Button
                onClick={() => alert("모른다면 지금부터 알아보도록 합시다.")}
              >
                No
              </Button>
            </ButtonContainer>
          </ModalContainer>
        </ModalBackground>
      )}
    </WholeBack>
  );
}

export default PortfolioModal;
