import React, { useState } from "react";
import styled from "styled-components";
import Modal from "./commons/Modalportfolio";
import earth from "./photo/earth.png";
import knocklogo from "./photo/knocklogo.png";

const WholeBack = styled.div`
  display: flex;
  justify-content: space-between;
  padding-top: 5rem;
`;

const LeftSection = styled.div`
  flex: 1;
  padding: 20px;
`;

const RightSection = styled.div`
  flex: 1;
  padding: 20px;
`;

const ImageContainer = styled.div`
  cursor: pointer;

  &:hover {
    transform: translateZ(10px); /* 이미지를 앞으로 튀어나오게 만듭니다. */
  }
`;

const ImgBox = styled.div`
  display: flex;
  align-items: row;
  img {
    width: 10rem;
    height: auto;
  }
`;

function PortfolioModal() {
  const [isModalOpen, setIsModalOpen] = useState(true);
  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <>
      {isModalOpen && <Modal closeModal={closeModal} />}
      {!isModalOpen && (
        <WholeBack>
          <LeftSection>
            <h1>Skills</h1>
            <ImageContainer>
              <h2>FrontEnd</h2>
              <ImgBox>
                <img src={earth} alt="Front-end" />
                <img src={knocklogo} alt="Front-end" />
              </ImgBox>
            </ImageContainer>
            <h2>Data</h2>
            <img src="Data_image.jpg" alt="Data" />
            <ImageContainer>
              {/* Data 이미지를 여기에 넣으세요 */}
              <img src="data_image.jpg" alt="Data" />
            </ImageContainer>
          </LeftSection>
          <RightSection>
            <h1>Experience</h1>
            <ImageContainer>
              <h2>Project</h2>
              {/* Project 이미지를 여기에 넣으세요 */}
              <img src="project_image.jpg" alt="Project" />
            </ImageContainer>
          </RightSection>
        </WholeBack>
      )}
    </>
  );
}

export default PortfolioModal;
