import React from "react";
import styled from "styled-components";
import yujinjapan from "./photo/yujinjapan.mp4";
import tape1 from "./photo/tape1.png";
import tape2 from "./photo/tape2.png";
import tape3 from "./photo/tape3.png";

const WholeBack = styled.div`
  background-color: #abc5d8;
  width: 100%;
  height: 100vh;
  padding-top: 10rem;
  position: relative; /* 자식 엘리먼트의 위치를 상대적으로 설정하기 위해 필요합니다. */
`;

const VideoContainer = styled.div`
  display: flex;
  justify-content: center;
  width: 80%;
  margin-left: -7rem;
  position: relative; /* 자식 엘리먼트의 위치를 상대적으로 설정하기 위해 필요합니다. */
`;

const Video = styled.video`
  width: 35rem;
  height: 45vh;
  box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.5);
`;

const TapeImage = styled.img`
  width: 10rem;
  height: auto;
  position: absolute;
  transform: rotate(45deg); /* 이미지를 45도 회전시킵니다. */
  /* 각 모서리에 따라 위치 조정 */
  &.top-left {
    top: -4rem;
    left: 10rem;
    transform: rotate(-45deg);
  }

  &.top-right {
    top: -4rem;
    right: 9rem;
  }

  &.bottom-left {
    bottom: -2rem;
    left: 14.5rem;
    transform: rotate(45deg); /* 왼쪽 아래 모서리는 반대 방향으로 45도 회전 */
  }

  &.bottom-right {
    bottom: -4rem;
    right: 13rem;
    transform: rotate(
      -45deg
    ); /* 오른쪽 아래 모서리는 반대 방향으로 45도 회전 */
  }
`;

function About() {
  return (
    <WholeBack>
      <VideoContainer>
        <Video autoPlay loop muted>
          <source src={yujinjapan} type="video/mp4" />
          브라우저가 HTML5 비디오를 지원하지 않을 때 표시되는 메시지
        </Video>
        {/* 테이프 이미지를 각 모서리에 추가 */}
        <TapeImage className="top-left" src={tape1} alt="Tape" />
        <TapeImage className="top-right" src={tape2} alt="Tape" />
        <TapeImage className="bottom-left" src={tape3} alt="Tape" />
        <TapeImage className="bottom-right" src={tape1} alt="Tape" />
      </VideoContainer>
    </WholeBack>
  );
}

export default About;
